                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
                        v_lz += fzl * prod_xy;
                        if (do_k) {
                            dd  = dm[(j0+0)*nao+k0+2] * dm[(i0+2)*nao+l0+2];
                            dd += dm[(j0+0)*nao+l0+2] * dm[(i0+2)*nao+k0+2];
                            if (jk.n_dm > 1) {
                                dd += dm[(nao+j0+0)*nao+k0+2] * dm[(nao+i0+2)*nao+l0+2];
                                dd += dm[(nao+j0+0)*nao+l0+2] * dm[(nao+i0+2)*nao+k0+2];
                            }
                            dd *= jk.k_factor;
                        } else {
                            dd = 0.;
                        }
                        if (do_j) {
                            if (jk.n_dm == 1) {
                                dd += jk.j_factor * dm[(j0+0)*nao+i0+2] * dm[(l0+2)*nao+k0+2];
                            } else {
                                int ji = (j0+0)*nao+i0+2;
                                int lk = (l0+2)*nao+k0+2;
                                dd += jk.j_factor * (dm[ji] + dm[nao*nao+ji]) * (dm[lk] + dm[nao*nao+lk]);
                            }
                        }
                        prod_xy = fac * 1 * dd;
                        prod_xz = fac * hrr_1011z * dd;
                        prod_yz = 1 * hrr_1011z * dd;
                        fxi = ai2 * trr_10x;
                        fyi = ai2 * trr_10y;
                        double trr_22z = cpz * trr_21z + 1*b01 * trr_20z + 2*b00 * trr_11z;
                        double hrr_2011z = trr_22z - zlzk * trr_21z;
                        fzi = ai2 * hrr_2011z;
                        fxj = aj2 * hrr_0100x;
                        fyj = aj2 * hrr_0100y;
                        double hrr_1111z = hrr_2011z - zjzi * hrr_1011z;
                        fzj = aj2 * hrr_1111z;
                        fxk = ak2 * trr_01x;
                        fyk = ak2 * trr_01y;
                        double trr_13z = cpz * trr_12z + 2*b01 * trr_11z + 1*b00 * trr_02z;
                        double hrr_1021z = trr_13z - zlzk * trr_12z;
                        fzk = ak2 * hrr_1021z;
                        fxl = al2 * hrr_0001x;
                        fyl = al2 * hrr_0001y;
                        double hrr_1012z = hrr_1021z - zlzk * hrr_1011z;
                        fzl = al2 * hrr_1012z;
                        fzi -= 1 * hrr_0011z;
                        fzk -= 1 * hrr_1001z;
                        fzl -= 1 * trr_11z;
                        v_ix += fxi * prod_yz;
                        v_iy += fyi * prod_xz;
                        v_iz += fzi * prod_xy;
                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
                        v_lz += fzl * prod_xy;
