                        fxi = ai2 * hrr_1111x;
                        double trr_20y = c0y * trr_10y + 1*b10 * 1;
                        fyi = ai2 * trr_20y;
                        fzi = ai2 * trr_10z;
                        double hrr_0211x = hrr_1111x - xjxi * hrr_0111x;
                        fxj = aj2 * hrr_0211x;
                        double hrr_1100y = trr_20y - yjyi * trr_10y;
