                        fzl -= 1 * trr_01z;
                        v_ix += fxi * prod_yz;
                        v_iy += fyi * prod_xz;
                        v_iz += fzi * prod_xy;
                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
                        v_lz += fzl * prod_xy;
                        if (do_k) {
                            dd  = dm[(j0+1)*nao+k0+2] * dm[(i0+1)*nao+l0+2];
                            dd += dm[(j0+1)*nao+l0+2] * dm[(i0+1)*nao+k0+2];
                            if (jk.n_dm > 1) {
                                dd += dm[(nao+j0+1)*nao+k0+2] * dm[(nao+i0+1)*nao+l0+2];
                                dd += dm[(nao+j0+1)*nao+l0+2] * dm[(nao+i0+1)*nao+k0+2];
                            }
                            dd *= jk.k_factor;
                        } else {
                            dd = 0.;
                        }
                        if (do_j) {
                            if (jk.n_dm == 1) {
                                dd += jk.j_factor * dm[(j0+1)*nao+i0+1] * dm[(l0+2)*nao+k0+2];
                            } else {
                                int ji = (j0+1)*nao+i0+1;
                                int lk = (l0+2)*nao+k0+2;
                                dd += jk.j_factor * (dm[ji] + dm[nao*nao+ji]) * (dm[lk] + dm[nao*nao+lk]);
                            }
                        }
                        prod_xy = fac * hrr_1100y * dd;
                        prod_xz = fac * hrr_0011z * dd;
                        prod_yz = hrr_1100y * hrr_0011z * dd;
                        fxi = ai2 * trr_10x;
                        fyi = ai2 * hrr_2100y;
                        fzi = ai2 * hrr_1011z;
                        fxj = aj2 * hrr_0100x;
                        fyj = aj2 * hrr_1200y;
                        fzj = aj2 * hrr_0111z;
                        fxk = ak2 * trr_01x;
                        fyk = ak2 * hrr_1110y;
                        fzk = ak2 * hrr_0021z;
                        fxl = al2 * hrr_0001x;
                        fyl = al2 * hrr_1101y;
                        fzl = al2 * hrr_0012z;
                        fyi -= 1 * hrr_0100y;
                        fyj -= 1 * trr_10y;
                        fzk -= 1 * hrr_0001z;
                        fzl -= 1 * trr_01z;
                        v_ix += fxi * prod_yz;
                        v_iy += fyi * prod_xz;
                        v_iz += fzi * prod_xy;
                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
                        v_lz += fzl * prod_xy;
                        if (do_k) {
                            dd  = dm[(j0+1)*nao+k0+2] * dm[(i0+2)*nao+l0+2];
                            dd += dm[(j0+1)*nao+l0+2] * dm[(i0+2)*nao+k0+2];
                            if (jk.n_dm > 1) {
                                dd += dm[(nao+j0+1)*nao+k0+2] * dm[(nao+i0+2)*nao+l0+2];
                                dd += dm[(nao+j0+1)*nao+l0+2] * dm[(nao+i0+2)*nao+k0+2];
                            }
                            dd *= jk.k_factor;
                        } else {
                            dd = 0.;
                        }
                        if (do_j) {
                            if (jk.n_dm == 1) {
                                dd += jk.j_factor * dm[(j0+1)*nao+i0+2] * dm[(l0+2)*nao+k0+2];
                            } else {
                                int ji = (j0+1)*nao+i0+2;
                                int lk = (l0+2)*nao+k0+2;
                                dd += jk.j_factor * (dm[ji] + dm[nao*nao+ji]) * (dm[lk] + dm[nao*nao+lk]);
                            }
                        }
                        prod_xy = fac * hrr_0100y * dd;
                        prod_xz = fac * hrr_1011z * dd;
                        prod_yz = hrr_0100y * hrr_1011z * dd;
                        fxi = ai2 * trr_10x;
                        fyi = ai2 * hrr_1100y;
                        fzi = ai2 * hrr_2011z;
                        fxj = aj2 * hrr_0100x;
                        fyj = aj2 * hrr_0200y;
                        fzj = aj2 * hrr_1111z;
                        fxk = ak2 * trr_01x;
                        fyk = ak2 * hrr_0110y;
                        fzk = ak2 * hrr_1021z;
                        fxl = al2 * hrr_0001x;
                        fyl = al2 * hrr_0101y;
                        fzl = al2 * hrr_1012z;
                        fzi -= 1 * hrr_0011z;
                        fyj -= 1 * 1;
                        fzk -= 1 * hrr_1001z;
                        fzl -= 1 * trr_11z;
                        v_ix += fxi * prod_yz;
                        v_iy += fyi * prod_xz;
                        v_iz += fzi * prod_xy;
                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
                        v_lz += fzl * prod_xy;
                        if (do_k) {
                            dd  = dm[(j0+2)*nao+k0+2] * dm[(i0+0)*nao+l0+2];
                            dd += dm[(j0+2)*nao+l0+2] * dm[(i0+0)*nao+k0+2];
                            if (jk.n_dm > 1) {
                                dd += dm[(nao+j0+2)*nao+k0+2] * dm[(nao+i0+0)*nao+l0+2];
                                dd += dm[(nao+j0+2)*nao+l0+2] * dm[(nao+i0+0)*nao+k0+2];
                            }
                            dd *= jk.k_factor;
                        } else {
                            dd = 0.;
                        }
                        if (do_j) {
                            if (jk.n_dm == 1) {
                                dd += jk.j_factor * dm[(j0+2)*nao+i0+0] * dm[(l0+2)*nao+k0+2];
                            } else {
                                int ji = (j0+2)*nao+i0+0;
                                int lk = (l0+2)*nao+k0+2;
                                dd += jk.j_factor * (dm[ji] + dm[nao*nao+ji]) * (dm[lk] + dm[nao*nao+lk]);
                            }
                        }
                        prod_xy = trr_10x * 1 * dd;
                        prod_xz = trr_10x * hrr_0111z * dd;
                        prod_yz = 1 * hrr_0111z * dd;
                        fxi = ai2 * trr_20x;
                        fyi = ai2 * trr_10y;
                        fzi = ai2 * hrr_1111z;
                        fxj = aj2 * hrr_1100x;
                        fyj = aj2 * hrr_0100y;
                        double hrr_0211z = hrr_1111z - zjzi * hrr_0111z;
                        fzj = aj2 * hrr_0211z;
                        fxk = ak2 * trr_11x;
                        fyk = ak2 * trr_01y;
                        double hrr_0121z = hrr_1021z - zjzi * hrr_0021z;
                        fzk = ak2 * hrr_0121z;
                        fxl = al2 * hrr_1001x;
                        fyl = al2 * hrr_0001y;
                        double hrr_0112z = hrr_1012z - zjzi * hrr_0012z;
                        fzl = al2 * hrr_0112z;
                        fxi -= 1 * fac;
                        fzj -= 1 * hrr_0011z;
                        fzk -= 1 * hrr_0101z;
                        fzl -= 1 * hrr_0110z;
                        v_ix += fxi * prod_yz;
                        v_iy += fyi * prod_xz;
                        v_iz += fzi * prod_xy;
                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
                        v_lz += fzl * prod_xy;
                        if (do_k) {
                            dd  = dm[(j0+2)*nao+k0+2] * dm[(i0+1)*nao+l0+2];
                            dd += dm[(j0+2)*nao+l0+2] * dm[(i0+1)*nao+k0+2];
                            if (jk.n_dm > 1) {
                                dd += dm[(nao+j0+2)*nao+k0+2] * dm[(nao+i0+1)*nao+l0+2];
                                dd += dm[(nao+j0+2)*nao+l0+2] * dm[(nao+i0+1)*nao+k0+2];
                            }
                            dd *= jk.k_factor;
                        } else {
                            dd = 0.;
                        }
                        if (do_j) {
                            if (jk.n_dm == 1) {
                                dd += jk.j_factor * dm[(j0+2)*nao+i0+1] * dm[(l0+2)*nao+k0+2];
                            } else {
                                int ji = (j0+2)*nao+i0+1;
                                int lk = (l0+2)*nao+k0+2;
                                dd += jk.j_factor * (dm[ji] + dm[nao*nao+ji]) * (dm[lk] + dm[nao*nao+lk]);
                            }
                        }
                        prod_xy = fac * trr_10y * dd;
                        prod_xz = fac * hrr_0111z * dd;
                        prod_yz = trr_10y * hrr_0111z * dd;
                        fxi = ai2 * trr_10x;
                        fyi = ai2 * trr_20y;
                        fzi = ai2 * hrr_1111z;
                        fxj = aj2 * hrr_0100x;
                        fyj = aj2 * hrr_1100y;
                        fzj = aj2 * hrr_0211z;
                        fxk = ak2 * trr_01x;
                        fyk = ak2 * trr_11y;
                        fzk = ak2 * hrr_0121z;
                        fxl = al2 * hrr_0001x;
                        fyl = al2 * hrr_1001y;
                        fzl = al2 * hrr_0112z;
                        fyi -= 1 * 1;
                        fzj -= 1 * hrr_0011z;
                        fzk -= 1 * hrr_0101z;
                        fzl -= 1 * hrr_0110z;
                        v_ix += fxi * prod_yz;
                        v_iy += fyi * prod_xz;
                        v_iz += fzi * prod_xy;
                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
                        v_lz += fzl * prod_xy;
                        if (do_k) {
                            dd  = dm[(j0+2)*nao+k0+2] * dm[(i0+2)*nao+l0+2];
                            dd += dm[(j0+2)*nao+l0+2] * dm[(i0+2)*nao+k0+2];
                            if (jk.n_dm > 1) {
                                dd += dm[(nao+j0+2)*nao+k0+2] * dm[(nao+i0+2)*nao+l0+2];
                                dd += dm[(nao+j0+2)*nao+l0+2] * dm[(nao+i0+2)*nao+k0+2];
                            }
                            dd *= jk.k_factor;
                        } else {
                            dd = 0.;
                        }
                        if (do_j) {
                            if (jk.n_dm == 1) {
                                dd += jk.j_factor * dm[(j0+2)*nao+i0+2] * dm[(l0+2)*nao+k0+2];
                            } else {
                                int ji = (j0+2)*nao+i0+2;
                                int lk = (l0+2)*nao+k0+2;
                                dd += jk.j_factor * (dm[ji] + dm[nao*nao+ji]) * (dm[lk] + dm[nao*nao+lk]);
                            }
                        }
                        prod_xy = fac * 1 * dd;
                        prod_xz = fac * hrr_1111z * dd;
                        prod_yz = 1 * hrr_1111z * dd;
                        fxi = ai2 * trr_10x;
                        fyi = ai2 * trr_10y;
                        double trr_32z = cpz * trr_31z + 1*b01 * trr_30z + 3*b00 * trr_21z;
                        double hrr_3011z = trr_32z - zlzk * trr_31z;
                        double hrr_2111z = hrr_3011z - zjzi * hrr_2011z;
                        fzi = ai2 * hrr_2111z;
                        fxj = aj2 * hrr_0100x;
                        fyj = aj2 * hrr_0100y;
                        double hrr_1211z = hrr_2111z - zjzi * hrr_1111z;
                        fzj = aj2 * hrr_1211z;
                        fxk = ak2 * trr_01x;
                        fyk = ak2 * trr_01y;
                        double trr_23z = cpz * trr_22z + 2*b01 * trr_21z + 2*b00 * trr_12z;
                        double hrr_2021z = trr_23z - zlzk * trr_22z;
                        double hrr_1121z = hrr_2021z - zjzi * hrr_1021z;
                        fzk = ak2 * hrr_1121z;
                        fxl = al2 * hrr_0001x;
                        fyl = al2 * hrr_0001y;
                        double hrr_2012z = hrr_2021z - zlzk * hrr_2011z;
                        double hrr_1112z = hrr_2012z - zjzi * hrr_1012z;
                        fzl = al2 * hrr_1112z;
                        fzi -= 1 * hrr_0111z;
                        fzj -= 1 * hrr_1011z;
                        fzk -= 1 * hrr_1101z;
                        fzl -= 1 * hrr_1110z;
                        v_ix += fxi * prod_yz;
                        v_iy += fyi * prod_xz;
                        v_iz += fzi * prod_xy;
                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
                        v_lz += fzl * prod_xy;
                    }
                }
            }
        }
        if (task_id >= ntasks) {
            continue;
        }
        int ia = bas[ish*BAS_SLOTS+ATOM_OF];
        int ja = bas[jsh*BAS_SLOTS+ATOM_OF];
        int ka = bas[ksh*BAS_SLOTS+ATOM_OF];
        int la = bas[lsh*BAS_SLOTS+ATOM_OF];
        double *ejk = jk.ejk;
        atomicAdd(ejk+ia*3+0, v_ix);
        atomicAdd(ejk+ia*3+1, v_iy);
        atomicAdd(ejk+ia*3+2, v_iz);
        atomicAdd(ejk+ja*3+0, v_jx);
        atomicAdd(ejk+ja*3+1, v_jy);
        atomicAdd(ejk+ja*3+2, v_jz);
        atomicAdd(ejk+ka*3+0, v_kx);
        atomicAdd(ejk+ka*3+1, v_ky);
        atomicAdd(ejk+ka*3+2, v_kz);
        atomicAdd(ejk+la*3+0, v_lx);
        atomicAdd(ejk+la*3+1, v_ly);
        atomicAdd(ejk+la*3+2, v_lz);
    }
}
__global__
void rys_ejk_ip1_1111(RysIntEnvVars envs, JKEnergy jk, BoundsInfo bounds,
                ShellQuartet *pool, uint32_t *batch_head)
{
    int b_id = blockIdx.x;
    int t_id = threadIdx.x + blockDim.x * threadIdx.y;
    ShellQuartet *shl_quartet_idx = pool + b_id * QUEUE_DEPTH;
    __shared__ int batch_id;
    if (t_id == 0) {
        batch_id = atomicAdd(batch_head, 1);
    }
    __syncthreads();
    int nbatches_kl = (bounds.ntile_kl_pairs + TILES_IN_BATCH - 1) / TILES_IN_BATCH;
    int nbatches = bounds.ntile_ij_pairs * nbatches_kl;
    while (batch_id < nbatches) {
        int batch_ij = batch_id / nbatches_kl;
        int batch_kl = batch_id % nbatches_kl;
        int nbas = envs.nbas;
        int ntasks = _fill_ejk_tasks(shl_quartet_idx, envs, jk, bounds,
                                     batch_ij, batch_kl);
        if (ntasks > 0) {
            int tile_ij = bounds.tile_ij_mapping[batch_ij];
            int nbas_tiles = nbas / TILE;
            int tile_i = tile_ij / nbas_tiles;
            int tile_j = tile_ij % nbas_tiles;
            int ish0 = tile_i * TILE;
            int jsh0 = tile_j * TILE;
            _rys_ejk_ip1_1111(envs, jk, bounds, shl_quartet_idx, ntasks, ish0, jsh0);
        }
        if (t_id == 0) {
            batch_id = atomicAdd(batch_head, 1);
            atomicAdd(batch_head+1, ntasks);
        }
        __syncthreads();
    }
}

__device__ static
void _rys_ejk_ip1_2000(RysIntEnvVars envs, JKEnergy jk, BoundsInfo bounds,
                ShellQuartet *shl_quartet_idx, int ntasks, int ish0, int jsh0)
{
    int sq_id = threadIdx.x + blockDim.x * threadIdx.y;
    int nsq_per_block = blockDim.x * blockDim.y;
    int iprim = bounds.iprim;
    int jprim = bounds.jprim;
    int kprim = bounds.kprim;
    int lprim = bounds.lprim;
    int *ao_loc = envs.ao_loc;
    int nbas = envs.nbas;
    int nao = ao_loc[nbas];
    int *bas = envs.bas;
    double *env = envs.env;
    double omega = env[PTR_RANGE_OMEGA];
    int do_j = jk.j_factor != 0.;
    int do_k = jk.k_factor != 0.;
    double *dm = jk.dm;
    extern __shared__ double Rpa_cicj[];
    double *rw = Rpa_cicj + iprim*jprim*TILE2*4;
    for (int n = sq_id; n < iprim*jprim*TILE2; n += nsq_per_block) {
        int ijp = n / TILE2;
        int sh_ij = n % TILE2;
        int ish = ish0 + sh_ij / TILE;
        int jsh = jsh0 + sh_ij % TILE;
        int ip = ijp / jprim;
        int jp = ijp % jprim;
        double *expi = env + bas[ish*BAS_SLOTS+PTR_EXP];
        double *expj = env + bas[jsh*BAS_SLOTS+PTR_EXP];
        double *ci = env + bas[ish*BAS_SLOTS+PTR_COEFF];
        double *cj = env + bas[jsh*BAS_SLOTS+PTR_COEFF];
        double *ri = env + bas[ish*BAS_SLOTS+PTR_BAS_COORD];
        double *rj = env + bas[jsh*BAS_SLOTS+PTR_BAS_COORD];
        double ai = expi[ip];
        double aj = expj[jp];
        double aij = ai + aj;
        double aj_aij = aj / aij;
        double xjxi = rj[0] - ri[0];
        double yjyi = rj[1] - ri[1];
        double zjzi = rj[2] - ri[2];
        double *Rpa = Rpa_cicj + ijp * TILE2*4;
        Rpa[sh_ij+0*TILE2] = xjxi * aj_aij;
        Rpa[sh_ij+1*TILE2] = yjyi * aj_aij;
        Rpa[sh_ij+2*TILE2] = zjzi * aj_aij;
        double theta_ij = ai * aj / aij;
        double Kab = exp(-theta_ij * (xjxi*xjxi+yjyi*yjyi+zjzi*zjzi));
        Rpa[sh_ij+3*TILE2] = ci[ip] * cj[jp] * Kab;
    }

    for (int task0 = 0; task0 < ntasks; task0 += nsq_per_block) {
        __syncthreads();
        int task_id = task0 + sq_id;
        double fac_sym = PI_FAC;
        ShellQuartet sq;
        if (task_id >= ntasks) {
            // To avoid __syncthreads blocking blocking idle warps, all remaining
            // threads compute a valid shell quartet with zero normalization factor
            sq = shl_quartet_idx[0];
            fac_sym = 0.;
        } else {
            sq = shl_quartet_idx[task_id];
        }
        int ish = sq.i;
        int jsh = sq.j;
        int ksh = sq.k;
        int lsh = sq.l;
        int sh_ij = (ish % TILE) * TILE + (jsh % TILE);
        if (ish == jsh) fac_sym *= .5;
        if (ksh == lsh) fac_sym *= .5;
        if (ish*nbas+jsh == ksh*nbas+lsh) fac_sym *= .5;
        int i0 = ao_loc[ish];
        int j0 = ao_loc[jsh];
        int k0 = ao_loc[ksh];
        int l0 = ao_loc[lsh];
        double *expi = env + bas[ish*BAS_SLOTS+PTR_EXP];
        double *expj = env + bas[jsh*BAS_SLOTS+PTR_EXP];
        double *expk = env + bas[ksh*BAS_SLOTS+PTR_EXP];
        double *expl = env + bas[lsh*BAS_SLOTS+PTR_EXP];
        double *ck = env + bas[ksh*BAS_SLOTS+PTR_COEFF];
        double *cl = env + bas[lsh*BAS_SLOTS+PTR_COEFF];
        double *ri = env + bas[ish*BAS_SLOTS+PTR_BAS_COORD];
        double *rj = env + bas[jsh*BAS_SLOTS+PTR_BAS_COORD];
        double *rk = env + bas[ksh*BAS_SLOTS+PTR_BAS_COORD];
        double *rl = env + bas[lsh*BAS_SLOTS+PTR_BAS_COORD];
        double v_ix = 0;
        double v_iy = 0;
        double v_iz = 0;
        double v_jx = 0;
        double v_jy = 0;
        double v_jz = 0;
        double v_kx = 0;
        double v_ky = 0;
        double v_kz = 0;
        double v_lx = 0;
        double v_ly = 0;
        double v_lz = 0;
        double dd, prod_xy, prod_xz, prod_yz;
        double fxi;
        double fyi;
        double fzi;
        double fxj;
        double fyj;
        double fzj;
        double fxk;
        double fyk;
        double fzk;
        double fxl;
        double fyl;
        double fzl;
        for (int klp = 0; klp < kprim*lprim; ++klp) {
            int kp = klp / lprim;
            int lp = klp % lprim;
            double ak = expk[kp];
            double al = expl[lp];
            double ak2 = ak * 2;
            double al2 = al * 2;
            double akl = ak + al;
            double al_akl = al / akl;
            double xlxk = rl[0] - rk[0];
            double ylyk = rl[1] - rk[1];
            double zlzk = rl[2] - rk[2];
            double theta_kl = ak * al / akl;
            double Kcd = exp(-theta_kl * (xlxk*xlxk+ylyk*ylyk+zlzk*zlzk));
            double ckcl = fac_sym * ck[kp] * cl[lp] * Kcd;
            double xqc = xlxk * al_akl;
            double yqc = ylyk * al_akl;
            double zqc = zlzk * al_akl;
            for (int ijp = 0; ijp < iprim*jprim; ++ijp) {
                int ip = ijp / jprim;
                int jp = ijp % jprim;
                double ai = expi[ip];
                double aj = expj[jp];
                double ai2 = ai * 2;
                double aj2 = aj * 2;
                double aij = ai + aj;
                double xjxi = rj[0] - ri[0];
                double yjyi = rj[1] - ri[1];
                double zjzi = rj[2] - ri[2];
                double *Rpa = Rpa_cicj + ijp * TILE2*4;
                double cicj = Rpa[sh_ij+3*TILE2];
                double fac = cicj * ckcl / (aij*akl*sqrt(aij+akl));
                double xpa = Rpa[sh_ij+0*TILE2];
                double ypa = Rpa[sh_ij+1*TILE2];
                double zpa = Rpa[sh_ij+2*TILE2];
                double xij = ri[0] + xpa; // (ai*xi+aj*xj)/aij
                double yij = ri[1] + ypa;
                double zij = ri[2] + zpa;
                double xkl = rk[0] + xqc;
                double ykl = rk[1] + yqc;
                double zkl = rk[2] + zqc;
                double xpq = xij - xkl;
                double ypq = yij - ykl;
                double zpq = zij - zkl;
                double theta = aij * akl / (aij + akl);
                double rr = xpq * xpq + ypq * ypq + zpq * zpq;
                double theta_rr = theta * rr;
                if (omega == 0) {
                    rys_roots(2, theta_rr, rw);
                } else if (omega > 0) {
                    double theta_fac = omega * omega / (omega * omega + theta);
                    rys_roots(2, theta_fac*theta_rr, rw);
                    fac *= sqrt(theta_fac);
                    for (int irys = 0; irys < 2; ++irys) {
                        rw[sq_id+ irys*2   *nsq_per_block] *= theta_fac;
                    }
                } else {
                    rys_roots(2, theta_rr, rw+4*nsq_per_block);
                    double theta_fac = omega * omega / (omega * omega + theta);
                    rys_roots(2, theta_fac*theta_rr, rw);
                    double sqrt_theta_fac = -sqrt(theta_fac);
                    for (int irys = 0; irys < 2; ++irys) {
                        rw[sq_id+ irys*2   *nsq_per_block] *= theta_fac;
                        rw[sq_id+(irys*2+1)*nsq_per_block] *= sqrt_theta_fac;
                    }
                }
                if (task_id < ntasks) {
                    for (int irys = 0; irys < bounds.nroots; ++irys) {
                        double wt = rw[sq_id + (2*irys+1)*nsq_per_block];
                        double rt = rw[sq_id +  2*irys   *nsq_per_block];
                        double rt_aa = rt / (aij + akl);
                        double rt_aij = rt_aa * akl;
                        double c0x = xpa - xpq*rt_aij;
                        double trr_10x = c0x * fac;
                        double b10 = .5/aij * (1 - rt_aij);
                        double trr_20x = c0x * trr_10x + 1*b10 * fac;
                        if (do_k) {
                            dd  = dm[(j0+0)*nao+k0+0] * dm[(i0+0)*nao+l0+0];
                            dd += dm[(j0+0)*nao+l0+0] * dm[(i0+0)*nao+k0+0];
                            if (jk.n_dm > 1) {
                                dd += dm[(nao+j0+0)*nao+k0+0] * dm[(nao+i0+0)*nao+l0+0];
                                dd += dm[(nao+j0+0)*nao+l0+0] * dm[(nao+i0+0)*nao+k0+0];
                            }
                            dd *= jk.k_factor;
                        } else {
                            dd = 0.;
                        }
                        if (do_j) {
                            if (jk.n_dm == 1) {
                                dd += jk.j_factor * dm[(j0+0)*nao+i0+0] * dm[(l0+0)*nao+k0+0];
                            } else {
                                int ji = (j0+0)*nao+i0+0;
                                int lk = (l0+0)*nao+k0+0;
                                dd += jk.j_factor * (dm[ji] + dm[nao*nao+ji]) * (dm[lk] + dm[nao*nao+lk]);
                            }
                        }
                        prod_xy = trr_20x * 1 * dd;
                        prod_xz = trr_20x * wt * dd;
                        prod_yz = 1 * wt * dd;
                        double trr_30x = c0x * trr_20x + 2*b10 * trr_10x;
                        fxi = ai2 * trr_30x;
                        double c0y = ypa - ypq*rt_aij;
                        double trr_10y = c0y * 1;
                        fyi = ai2 * trr_10y;
                        double c0z = zpa - zpq*rt_aij;
                        double trr_10z = c0z * wt;
                        fzi = ai2 * trr_10z;
                        double hrr_2100x = trr_30x - xjxi * trr_20x;
                        fxj = aj2 * hrr_2100x;
                        double hrr_0100y = trr_10y - yjyi * 1;
                        fyj = aj2 * hrr_0100y;
                        double hrr_0100z = trr_10z - zjzi * wt;
                        fzj = aj2 * hrr_0100z;
                        double rt_akl = rt_aa * aij;
                        double cpx = xqc + xpq*rt_akl;
                        double b00 = .5 * rt_aa;
                        double trr_21x = cpx * trr_20x + 2*b00 * trr_10x;
                        fxk = ak2 * trr_21x;
                        double cpy = yqc + ypq*rt_akl;
                        double trr_01y = cpy * 1;
                        fyk = ak2 * trr_01y;
                        double cpz = zqc + zpq*rt_akl;
                        double trr_01z = cpz * wt;
                        fzk = ak2 * trr_01z;
                        double hrr_2001x = trr_21x - xlxk * trr_20x;
                        fxl = al2 * hrr_2001x;
                        double hrr_0001y = trr_01y - ylyk * 1;
                        fyl = al2 * hrr_0001y;
                        double hrr_0001z = trr_01z - zlzk * wt;
                        fzl = al2 * hrr_0001z;
                        fxi -= 2 * trr_10x;
                        v_ix += fxi * prod_yz;
                        v_iy += fyi * prod_xz;
                        v_iz += fzi * prod_xy;
                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
                        v_lz += fzl * prod_xy;
                        if (do_k) {
                            dd  = dm[(j0+0)*nao+k0+0] * dm[(i0+1)*nao+l0+0];
                            dd += dm[(j0+0)*nao+l0+0] * dm[(i0+1)*nao+k0+0];
                            if (jk.n_dm > 1) {
                                dd += dm[(nao+j0+0)*nao+k0+0] * dm[(nao+i0+1)*nao+l0+0];
                                dd += dm[(nao+j0+0)*nao+l0+0] * dm[(nao+i0+1)*nao+k0+0];
                            }
                            dd *= jk.k_factor;
                        } else {
                            dd = 0.;
                        }
                        if (do_j) {
                            if (jk.n_dm == 1) {
                                dd += jk.j_factor * dm[(j0+0)*nao+i0+1] * dm[(l0+0)*nao+k0+0];
                            } else {
                                int ji = (j0+0)*nao+i0+1;
                                int lk = (l0+0)*nao+k0+0;
                                dd += jk.j_factor * (dm[ji] + dm[nao*nao+ji]) * (dm[lk] + dm[nao*nao+lk]);
                            }
                        }
                        prod_xy = trr_10x * trr_10y * dd;
                        prod_xz = trr_10x * wt * dd;
                        prod_yz = trr_10y * wt * dd;
                        fxi = ai2 * trr_20x;
                        double trr_20y = c0y * trr_10y + 1*b10 * 1;
                        fyi = ai2 * trr_20y;
                        fzi = ai2 * trr_10z;
                        double hrr_1100x = trr_20x - xjxi * trr_10x;
                        fxj = aj2 * hrr_1100x;
                        double hrr_1100y = trr_20y - yjyi * trr_10y;
                        fyj = aj2 * hrr_1100y;
                        fzj = aj2 * hrr_0100z;
                        double trr_11x = cpx * trr_10x + 1*b00 * fac;
                        fxk = ak2 * trr_11x;
                        double trr_11y = cpy * trr_10y + 1*b00 * 1;
                        fyk = ak2 * trr_11y;
                        fzk = ak2 * trr_01z;
                        double hrr_1001x = trr_11x - xlxk * trr_10x;
                        fxl = al2 * hrr_1001x;
                        double hrr_1001y = trr_11y - ylyk * trr_10y;
                        fyl = al2 * hrr_1001y;
                        fzl = al2 * hrr_0001z;
                        fxi -= 1 * fac;
                        fyi -= 1 * 1;
                        v_ix += fxi * prod_yz;
                        v_iy += fyi * prod_xz;
                        v_iz += fzi * prod_xy;
                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
                        v_lz += fzl * prod_xy;
                        if (do_k) {
                            dd  = dm[(j0+0)*nao+k0+0] * dm[(i0+2)*nao+l0+0];
                            dd += dm[(j0+0)*nao+l0+0] * dm[(i0+2)*nao+k0+0];
                            if (jk.n_dm > 1) {
                                dd += dm[(nao+j0+0)*nao+k0+0] * dm[(nao+i0+2)*nao+l0+0];
                                dd += dm[(nao+j0+0)*nao+l0+0] * dm[(nao+i0+2)*nao+k0+0];
                            }
                            dd *= jk.k_factor;
                        } else {
                            dd = 0.;
                        }
                        if (do_j) {
                            if (jk.n_dm == 1) {
                                dd += jk.j_factor * dm[(j0+0)*nao+i0+2] * dm[(l0+0)*nao+k0+0];
                            } else {
                                int ji = (j0+0)*nao+i0+2;
                                int lk = (l0+0)*nao+k0+0;
                                dd += jk.j_factor * (dm[ji] + dm[nao*nao+ji]) * (dm[lk] + dm[nao*nao+lk]);
                            }
                        }
                        prod_xy = trr_10x * 1 * dd;
                        prod_xz = trr_10x * trr_10z * dd;
                        prod_yz = 1 * trr_10z * dd;
                        fxi = ai2 * trr_20x;
                        fyi = ai2 * trr_10y;
                        double trr_20z = c0z * trr_10z + 1*b10 * wt;
                        fzi = ai2 * trr_20z;
                        fxj = aj2 * hrr_1100x;
                        fyj = aj2 * hrr_0100y;
                        double hrr_1100z = trr_20z - zjzi * trr_10z;
                        fzj = aj2 * hrr_1100z;
                        fxk = ak2 * trr_11x;
                        fyk = ak2 * trr_01y;
                        double trr_11z = cpz * trr_10z + 1*b00 * wt;
                        fzk = ak2 * trr_11z;
                        fxl = al2 * hrr_1001x;
                        fyl = al2 * hrr_0001y;
                        double hrr_1001z = trr_11z - zlzk * trr_10z;
                        fzl = al2 * hrr_1001z;
                        fxi -= 1 * fac;
                        fzi -= 1 * wt;
                        v_ix += fxi * prod_yz;
                        v_iy += fyi * prod_xz;
                        v_iz += fzi * prod_xy;
                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
                        v_lz += fzl * prod_xy;
                        if (do_k) {
                            dd  = dm[(j0+0)*nao+k0+0] * dm[(i0+3)*nao+l0+0];
                            dd += dm[(j0+0)*nao+l0+0] * dm[(i0+3)*nao+k0+0];
                            if (jk.n_dm > 1) {
                                dd += dm[(nao+j0+0)*nao+k0+0] * dm[(nao+i0+3)*nao+l0+0];
                                dd += dm[(nao+j0+0)*nao+l0+0] * dm[(nao+i0+3)*nao+k0+0];
                            }
                            dd *= jk.k_factor;
                        } else {
                            dd = 0.;
                        }
                        if (do_j) {
                            if (jk.n_dm == 1) {
                                dd += jk.j_factor * dm[(j0+0)*nao+i0+3] * dm[(l0+0)*nao+k0+0];
                            } else {
                                int ji = (j0+0)*nao+i0+3;
                                int lk = (l0+0)*nao+k0+0;
                                dd += jk.j_factor * (dm[ji] + dm[nao*nao+ji]) * (dm[lk] + dm[nao*nao+lk]);
                            }
                        }
                        prod_xy = fac * trr_20y * dd;
                        prod_xz = fac * wt * dd;
                        prod_yz = trr_20y * wt * dd;
                        fxi = ai2 * trr_10x;
                        double trr_30y = c0y * trr_20y + 2*b10 * trr_10y;
                        fyi = ai2 * trr_30y;
                        fzi = ai2 * trr_10z;
                        double hrr_0100x = trr_10x - xjxi * fac;
                        fxj = aj2 * hrr_0100x;
                        double hrr_2100y = trr_30y - yjyi * trr_20y;
                        fyj = aj2 * hrr_2100y;
                        fzj = aj2 * hrr_0100z;
                        double trr_01x = cpx * fac;
                        fxk = ak2 * trr_01x;
                        double trr_21y = cpy * trr_20y + 2*b00 * trr_10y;
                        fyk = ak2 * trr_21y;
                        fzk = ak2 * trr_01z;
                        double hrr_0001x = trr_01x - xlxk * fac;
                        fxl = al2 * hrr_0001x;
                        double hrr_2001y = trr_21y - ylyk * trr_20y;
                        fyl = al2 * hrr_2001y;
                        fzl = al2 * hrr_0001z;
                        fyi -= 2 * trr_10y;
                        v_ix += fxi * prod_yz;
                        v_iy += fyi * prod_xz;
                        v_iz += fzi * prod_xy;
                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
                        v_lz += fzl * prod_xy;
                        if (do_k) {
                            dd  = dm[(j0+0)*nao+k0+0] * dm[(i0+4)*nao+l0+0];
                            dd += dm[(j0+0)*nao+l0+0] * dm[(i0+4)*nao+k0+0];
                            if (jk.n_dm > 1) {
                                dd += dm[(nao+j0+0)*nao+k0+0] * dm[(nao+i0+4)*nao+l0+0];
                                dd += dm[(nao+j0+0)*nao+l0+0] * dm[(nao+i0+4)*nao+k0+0];
                            }
                            dd *= jk.k_factor;
                        } else {
                            dd = 0.;
                        }
                        if (do_j) {
                            if (jk.n_dm == 1) {
                                dd += jk.j_factor * dm[(j0+0)*nao+i0+4] * dm[(l0+0)*nao+k0+0];
                            } else {
                                int ji = (j0+0)*nao+i0+4;
                                int lk = (l0+0)*nao+k0+0;
                                dd += jk.j_factor * (dm[ji] + dm[nao*nao+ji]) * (dm[lk] + dm[nao*nao+lk]);
                            }
                        }
                        prod_xy = fac * trr_10y * dd;
                        prod_xz = fac * trr_10z * dd;
                        prod_yz = trr_10y * trr_10z * dd;
                        fxi = ai2 * trr_10x;
                        fyi = ai2 * trr_20y;
                        fzi = ai2 * trr_20z;
                        fxj = aj2 * hrr_0100x;
                        fyj = aj2 * hrr_1100y;
                        fzj = aj2 * hrr_1100z;
                        fxk = ak2 * trr_01x;
                        fyk = ak2 * trr_11y;
                        fzk = ak2 * trr_11z;
                        fxl = al2 * hrr_0001x;
                        fyl = al2 * hrr_1001y;
                        fzl = al2 * hrr_1001z;
                        fyi -= 1 * 1;
                        fzi -= 1 * wt;
                        v_ix += fxi * prod_yz;
                        v_iy += fyi * prod_xz;
                        v_iz += fzi * prod_xy;
                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
                        v_lz += fzl * prod_xy;
                        if (do_k) {
                            dd  = dm[(j0+0)*nao+k0+0] * dm[(i0+5)*nao+l0+0];
                            dd += dm[(j0+0)*nao+l0+0] * dm[(i0+5)*nao+k0+0];
                            if (jk.n_dm > 1) {
                                dd += dm[(nao+j0+0)*nao+k0+0] * dm[(nao+i0+5)*nao+l0+0];
                                dd += dm[(nao+j0+0)*nao+l0+0] * dm[(nao+i0+5)*nao+k0+0];
                            }
                            dd *= jk.k_factor;
                        } else {
                            dd = 0.;
                        }
                        if (do_j) {
                            if (jk.n_dm == 1) {
                                dd += jk.j_factor * dm[(j0+0)*nao+i0+5] * dm[(l0+0)*nao+k0+0];
                            } else {
                                int ji = (j0+0)*nao+i0+5;
                                int lk = (l0+0)*nao+k0+0;
                                dd += jk.j_factor * (dm[ji] + dm[nao*nao+ji]) * (dm[lk] + dm[nao*nao+lk]);
                            }
                        }
                        prod_xy = fac * 1 * dd;
                        prod_xz = fac * trr_20z * dd;
                        prod_yz = 1 * trr_20z * dd;
                        fxi = ai2 * trr_10x;
                        fyi = ai2 * trr_10y;
                        double trr_30z = c0z * trr_20z + 2*b10 * trr_10z;
                        fzi = ai2 * trr_30z;
                        fxj = aj2 * hrr_0100x;
                        fyj = aj2 * hrr_0100y;
                        double hrr_2100z = trr_30z - zjzi * trr_20z;
                        fzj = aj2 * hrr_2100z;
                        fxk = ak2 * trr_01x;
                        fyk = ak2 * trr_01y;
                        double trr_21z = cpz * trr_20z + 2*b00 * trr_10z;
                        fzk = ak2 * trr_21z;
                        fxl = al2 * hrr_0001x;
                        fyl = al2 * hrr_0001y;
                        double hrr_2001z = trr_21z - zlzk * trr_20z;
                        fzl = al2 * hrr_2001z;
                        fzi -= 2 * trr_10z;
                        v_ix += fxi * prod_yz;
                        v_iy += fyi * prod_xz;
                        v_iz += fzi * prod_xy;
                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
                        v_lz += fzl * prod_xy;
                    }
                }
            }
        }
        if (task_id >= ntasks) {
            continue;
        }
        int ia = bas[ish*BAS_SLOTS+ATOM_OF];
        int ja = bas[jsh*BAS_SLOTS+ATOM_OF];
        int ka = bas[ksh*BAS_SLOTS+ATOM_OF];
        int la = bas[lsh*BAS_SLOTS+ATOM_OF];
        double *ejk = jk.ejk;
        atomicAdd(ejk+ia*3+0, v_ix);
        atomicAdd(ejk+ia*3+1, v_iy);
        atomicAdd(ejk+ia*3+2, v_iz);
        atomicAdd(ejk+ja*3+0, v_jx);
        atomicAdd(ejk+ja*3+1, v_jy);
        atomicAdd(ejk+ja*3+2, v_jz);
        atomicAdd(ejk+ka*3+0, v_kx);
        atomicAdd(ejk+ka*3+1, v_ky);
        atomicAdd(ejk+ka*3+2, v_kz);
        atomicAdd(ejk+la*3+0, v_lx);
        atomicAdd(ejk+la*3+1, v_ly);
        atomicAdd(ejk+la*3+2, v_lz);
    }
}
__global__
void rys_ejk_ip1_2000(RysIntEnvVars envs, JKEnergy jk, BoundsInfo bounds,
                ShellQuartet *pool, uint32_t *batch_head)
{
    int b_id = blockIdx.x;
    int t_id = threadIdx.x + blockDim.x * threadIdx.y;
    ShellQuartet *shl_quartet_idx = pool + b_id * QUEUE_DEPTH;
    __shared__ int batch_id;
    if (t_id == 0) {
        batch_id = atomicAdd(batch_head, 1);
    }
    __syncthreads();
    int nbatches_kl = (bounds.ntile_kl_pairs + TILES_IN_BATCH - 1) / TILES_IN_BATCH;
    int nbatches = bounds.ntile_ij_pairs * nbatches_kl;
    while (batch_id < nbatches) {
        int batch_ij = batch_id / nbatches_kl;
        int batch_kl = batch_id % nbatches_kl;
        int nbas = envs.nbas;
        int ntasks = _fill_ejk_tasks(shl_quartet_idx, envs, jk, bounds,
                                     batch_ij, batch_kl);
        if (ntasks > 0) {
            int tile_ij = bounds.tile_ij_mapping[batch_ij];
            int nbas_tiles = nbas / TILE;
            int tile_i = tile_ij / nbas_tiles;
            int tile_j = tile_ij % nbas_tiles;
            int ish0 = tile_i * TILE;
            int jsh0 = tile_j * TILE;
            _rys_ejk_ip1_2000(envs, jk, bounds, shl_quartet_idx, ntasks, ish0, jsh0);
        }
        if (t_id == 0) {
            batch_id = atomicAdd(batch_head, 1);
            atomicAdd(batch_head+1, ntasks);
        }
        __syncthreads();
    }
}

__device__ static
void _rys_ejk_ip1_2010(RysIntEnvVars envs, JKEnergy jk, BoundsInfo bounds,
                ShellQuartet *shl_quartet_idx, int ntasks, int ish0, int jsh0)
{
    int sq_id = threadIdx.x + blockDim.x * threadIdx.y;
    int nsq_per_block = blockDim.x * blockDim.y;
    int iprim = bounds.iprim;
    int jprim = bounds.jprim;
    int kprim = bounds.kprim;
    int lprim = bounds.lprim;
    int *ao_loc = envs.ao_loc;
    int nbas = envs.nbas;
    int nao = ao_loc[nbas];
    int *bas = envs.bas;
    double *env = envs.env;
    double omega = env[PTR_RANGE_OMEGA];
    int do_j = jk.j_factor != 0.;
    int do_k = jk.k_factor != 0.;
    double *dm = jk.dm;
    extern __shared__ double Rpa_cicj[];
    double *rw = Rpa_cicj + iprim*jprim*TILE2*4;
    for (int n = sq_id; n < iprim*jprim*TILE2; n += nsq_per_block) {
        int ijp = n / TILE2;
        int sh_ij = n % TILE2;
        int ish = ish0 + sh_ij / TILE;
        int jsh = jsh0 + sh_ij % TILE;
        int ip = ijp / jprim;
        int jp = ijp % jprim;
        double *expi = env + bas[ish*BAS_SLOTS+PTR_EXP];
        double *expj = env + bas[jsh*BAS_SLOTS+PTR_EXP];
        double *ci = env + bas[ish*BAS_SLOTS+PTR_COEFF];
        double *cj = env + bas[jsh*BAS_SLOTS+PTR_COEFF];
        double *ri = env + bas[ish*BAS_SLOTS+PTR_BAS_COORD];
        double *rj = env + bas[jsh*BAS_SLOTS+PTR_BAS_COORD];
        double ai = expi[ip];
        double aj = expj[jp];
        double aij = ai + aj;
        double aj_aij = aj / aij;
        double xjxi = rj[0] - ri[0];
        double yjyi = rj[1] - ri[1];
        double zjzi = rj[2] - ri[2];
        double *Rpa = Rpa_cicj + ijp * TILE2*4;
        Rpa[sh_ij+0*TILE2] = xjxi * aj_aij;
        Rpa[sh_ij+1*TILE2] = yjyi * aj_aij;
        Rpa[sh_ij+2*TILE2] = zjzi * aj_aij;
        double theta_ij = ai * aj / aij;
        double Kab = exp(-theta_ij * (xjxi*xjxi+yjyi*yjyi+zjzi*zjzi));
        Rpa[sh_ij+3*TILE2] = ci[ip] * cj[jp] * Kab;
    }

    for (int task0 = 0; task0 < ntasks; task0 += nsq_per_block) {
        __syncthreads();
        int task_id = task0 + sq_id;
        double fac_sym = PI_FAC;
        ShellQuartet sq;
        if (task_id >= ntasks) {
            // To avoid __syncthreads blocking blocking idle warps, all remaining
            // threads compute a valid shell quartet with zero normalization factor
            sq = shl_quartet_idx[0];
            fac_sym = 0.;
        } else {
            sq = shl_quartet_idx[task_id];
        }
        int ish = sq.i;
        int jsh = sq.j;
        int ksh = sq.k;
        int lsh = sq.l;
        int sh_ij = (ish % TILE) * TILE + (jsh % TILE);
        if (ish == jsh) fac_sym *= .5;
        if (ksh == lsh) fac_sym *= .5;
        if (ish*nbas+jsh == ksh*nbas+lsh) fac_sym *= .5;
        int i0 = ao_loc[ish];
        int j0 = ao_loc[jsh];
        int k0 = ao_loc[ksh];
        int l0 = ao_loc[lsh];
        double *expi = env + bas[ish*BAS_SLOTS+PTR_EXP];
        double *expj = env + bas[jsh*BAS_SLOTS+PTR_EXP];
        double *expk = env + bas[ksh*BAS_SLOTS+PTR_EXP];
        double *expl = env + bas[lsh*BAS_SLOTS+PTR_EXP];
        double *ck = env + bas[ksh*BAS_SLOTS+PTR_COEFF];
        double *cl = env + bas[lsh*BAS_SLOTS+PTR_COEFF];
        double *ri = env + bas[ish*BAS_SLOTS+PTR_BAS_COORD];
        double *rj = env + bas[jsh*BAS_SLOTS+PTR_BAS_COORD];
        double *rk = env + bas[ksh*BAS_SLOTS+PTR_BAS_COORD];
        double *rl = env + bas[lsh*BAS_SLOTS+PTR_BAS_COORD];
        double v_ix = 0;
        double v_iy = 0;
        double v_iz = 0;
        double v_jx = 0;
        double v_jy = 0;
        double v_jz = 0;
        double v_kx = 0;
        double v_ky = 0;
        double v_kz = 0;
        double v_lx = 0;
        double v_ly = 0;
        double v_lz = 0;
        double dd, prod_xy, prod_xz, prod_yz;
        double fxi;
        double fyi;
        double fzi;
        double fxj;
        double fyj;
        double fzj;
        double fxk;
        double fyk;
        double fzk;
        double fxl;
        double fyl;
        double fzl;
        for (int klp = 0; klp < kprim*lprim; ++klp) {
            int kp = klp / lprim;
            int lp = klp % lprim;
            double ak = expk[kp];
            double al = expl[lp];
            double ak2 = ak * 2;
            double al2 = al * 2;
            double akl = ak + al;
            double al_akl = al / akl;
            double xlxk = rl[0] - rk[0];
            double ylyk = rl[1] - rk[1];
            double zlzk = rl[2] - rk[2];
            double theta_kl = ak * al / akl;
            double Kcd = exp(-theta_kl * (xlxk*xlxk+ylyk*ylyk+zlzk*zlzk));
            double ckcl = fac_sym * ck[kp] * cl[lp] * Kcd;
            double xqc = xlxk * al_akl;
            double yqc = ylyk * al_akl;
            double zqc = zlzk * al_akl;
            for (int ijp = 0; ijp < iprim*jprim; ++ijp) {
                int ip = ijp / jprim;
                int jp = ijp % jprim;
                double ai = expi[ip];
                double aj = expj[jp];
                double ai2 = ai * 2;
                double aj2 = aj * 2;
                double aij = ai + aj;
                double xjxi = rj[0] - ri[0];
                double yjyi = rj[1] - ri[1];
                double zjzi = rj[2] - ri[2];
                double *Rpa = Rpa_cicj + ijp * TILE2*4;
                double cicj = Rpa[sh_ij+3*TILE2];
                double fac = cicj * ckcl / (aij*akl*sqrt(aij+akl));
                double xpa = Rpa[sh_ij+0*TILE2];
                double ypa = Rpa[sh_ij+1*TILE2];
                double zpa = Rpa[sh_ij+2*TILE2];
                double xij = ri[0] + xpa; // (ai*xi+aj*xj)/aij
                double yij = ri[1] + ypa;
                double zij = ri[2] + zpa;
                double xkl = rk[0] + xqc;
                double ykl = rk[1] + yqc;
                double zkl = rk[2] + zqc;
                double xpq = xij - xkl;
                double ypq = yij - ykl;
                double zpq = zij - zkl;
                double theta = aij * akl / (aij + akl);
                double rr = xpq * xpq + ypq * ypq + zpq * zpq;
                double theta_rr = theta * rr;
                if (omega == 0) {
                    rys_roots(3, theta_rr, rw);
                } else if (omega > 0) {
                    double theta_fac = omega * omega / (omega * omega + theta);
                    rys_roots(3, theta_fac*theta_rr, rw);
                    fac *= sqrt(theta_fac);
                    for (int irys = 0; irys < 3; ++irys) {
                        rw[sq_id+ irys*2   *nsq_per_block] *= theta_fac;
                    }
                } else {
                    rys_roots(3, theta_rr, rw+6*nsq_per_block);
                    double theta_fac = omega * omega / (omega * omega + theta);
                    rys_roots(3, theta_fac*theta_rr, rw);
                    double sqrt_theta_fac = -sqrt(theta_fac);
                    for (int irys = 0; irys < 3; ++irys) {
                        rw[sq_id+ irys*2   *nsq_per_block] *= theta_fac;
                        rw[sq_id+(irys*2+1)*nsq_per_block] *= sqrt_theta_fac;
