                        prod_xy = trr_11x * trr_10y * dd;
                        prod_xz = trr_11x * wt * dd;
                        prod_yz = trr_10y * wt * dd;
                        fxi = ai2 * trr_21x;
                        double trr_20y = c0y * trr_10y + 1*b10 * 1;
                        fyi = ai2 * trr_20y;
                        fzi = ai2 * trr_10z;
                        double hrr_1110x = trr_21x - xjxi * trr_11x;
                        fxj = aj2 * hrr_1110x;
                        double hrr_1100y = trr_20y - yjyi * trr_10y;
                        fyj = aj2 * hrr_1100y;
                        fzj = aj2 * hrr_0100z;
                        double trr_01x = cpx * fac;
                        double trr_12x = cpx * trr_11x + 1*b01 * trr_10x + 1*b00 * trr_01x;
                        fxk = ak2 * trr_12x;
                        double trr_11y = cpy * trr_10y + 1*b00 * 1;
                        fyk = ak2 * trr_11y;
                        fzk = ak2 * trr_01z;
                        double hrr_1011x = trr_12x - xlxk * trr_11x;
                        fxl = al2 * hrr_1011x;
                        double hrr_1001y = trr_11y - ylyk * trr_10y;
                        fyl = al2 * hrr_1001y;
                        fzl = al2 * hrr_0001z;
                        fxi -= 1 * trr_01x;
                        fyi -= 1 * 1;
                        fxk -= 1 * trr_10x;
                        v_ix += fxi * prod_yz;
                        v_iy += fyi * prod_xz;
                        v_iz += fzi * prod_xy;
                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
