                        fxi = ai2 * trr_21x;
                        fyi = ai2 * trr_10y;
                        double trr_20z = c0z * trr_10z + 1*b10 * wt;
                        fzi = ai2 * trr_20z;
                        fxj = aj2 * hrr_1110x;
                        fyj = aj2 * hrr_0100y;
                        double hrr_1100z = trr_20z - zjzi * trr_10z;
                        fzj = aj2 * hrr_1100z;
                        fxk = ak2 * trr_12x;
                        fyk = ak2 * trr_01y;
