                        fzk = ak2 * trr_11z;
                        fxl = al2 * hrr_1011x;
                        fyl = al2 * hrr_0001y;
                        double hrr_1001z = trr_11z - zlzk * trr_10z;
                        fzl = al2 * hrr_1001z;
                        fxi -= 1 * trr_01x;
                        fzi -= 1 * wt;
                        fxk -= 1 * trr_10x;
                        v_ix += fxi * prod_yz;
                        v_iy += fyi * prod_xz;
                        v_iz += fzi * prod_xy;
                        v_jx += fxj * prod_yz;
                        v_jy += fyj * prod_xz;
                        v_jz += fzj * prod_xy;
                        v_kx += fxk * prod_yz;
                        v_ky += fyk * prod_xz;
                        v_kz += fzk * prod_xy;
                        v_lx += fxl * prod_yz;
                        v_ly += fyl * prod_xz;
